sections.css:
.px2-btn{
    display: inline-block;
    border-radius: 1px;
    background-color: rgba(255,255,255,0.6);
    color: #666;
    border: 1px solid #666;
    box-shadow: 0 2px 0px rgba(0,0,0,0.1);
    // text-shadow: 0px 0px 3px rgba(0,0,0,0.1);
    padding: 0.5em 2em;
    font-size:1em;
    line-height: 1;
    text-decoration: none;
    text-align: center;
    cursor: pointer;
    box-sizing: border-box;
    align-items: stretch;
    font-family: sans-serif;

    &:focus,
    &:hover{
        background-color: #666;
        color: #eee;
        outline: none;
    }
    &:active{
        box-shadow: inset 0 3px 15px rgba(0,0,0,0.3);
    }

    &[disabled]{
        border-color: #ddd;
        background-color: #f9f9f9;
        color: #ddd;
        cursor: default;
    }

    &.px2-btn--primary{
        border-color: #00a0e6;
        color: #00a0e6;
        &:focus,
        &:hover{
            background: #00a0e6;
            color: #fff;
        }
    }
    &.px2-btn--secondary{
        border-color: #999;
        color: #999;
        &:focus,
        &:hover{
            background: #999;
            color: #fff;
        }
    }
    &.px2-btn--danger{
        border-color: #e86a60;
        color: #e86a60;
        &:focus,
        &:hover{
            background: #e86a60;
            color: #fff;
        }
    }
    &.px2-btn--download{
        border-color: #4ad64d;
        color: #4ad64d;
        &:focus,
        &:hover{
            background: #4ad64d;
            color: #fff;
        }
    }

    &--sm{
        font-size:0.8em;
    }
    &--lg{
        font-size:1.2em;
    }
    &--block{
        display: block;
        width: 100%;
    }
}

@mixin px2-editor-type{
    display: inline-block;
    font-size: 12px;
    line-height: 1;
    padding: 0.4em 1em;
    text-align: center;
    color: #fff;
}
.px2-editor-type__html{
    @include px2-editor-type;
    background-color: #4a8a98;
    &:before{
        content: "HTML";
    }
}
.px2-editor-type__html-gui{
    @include px2-editor-type;
    background-color: #9f5b75;
    &:before{
        content: "GUI";
    }
}
.px2-editor-type__txt{
    @include px2-editor-type;
    background-color: #999;
    &:before{
        content: "TEXT";
    }
}
.px2-editor-type__md{
    @include px2-editor-type;
    background-color: #ae9b59;
    &:before{
        content: "Markdown";
    }
}
.px2-editor-type__jade{
    @include px2-editor-type;
    background-color: #999;
    &:before{
        content: "Jade";
    }
}
.px2-editor-type__not-exists{
    @include px2-editor-type;
    background-color: #c3c3c3;
    &:before{
        content: "未作成";
    }
}
.px2-editor-type__page-not-exists{
    @include px2-editor-type;
    background-color: #c3c3c3;
    &:before{
        content: "ページ未作成";
    }
}
.px2-editor-type--fullwidth{
    width: auto;
    display: block;
    float:none;
}

.px2-loading{
	clear: both;
	float: none;
	display: block;
	text-align:center;
	padding:80px;
}
.px2-loading:before{
	display: block;
	text-align: center;
	content: ' ';
	background-color: #000;
	width: 150px; height: 1px;
	margin: 0 auto;

	animation: broccoli--progress 1.4s infinite normal ease;
	@keyframes broccoli--progress {
		0% { transform: rotate(0deg) scale(0,0); }
		30% { transform: rotate(270deg) scale(1,1); }
		100% { transform: rotate(720deg) scale(0,0); }
	}
}

