sections.css:
.px2-link.px2-link--burette{
	&:before{
		content:' ';
		width:0;
		height:0;
		display: inline-block;
		margin-right: 0.2em;
		border-width:0.35em 0 0.35em 0.6em;
		border-style:solid;
		border-color:var(--px2-main-color);
		border-top-color:transparent;
		border-bottom-color:transparent;
	}
}

.px2-btn{
	display: inline-block;
	border-radius: 3px;
	background-color: #fff;
	color: #666;
	border: 1px solid #666;
	box-shadow: 0 2px 0px rgba(0,0,0,0.1);
	// text-shadow: 0px 0px 3px rgba(0,0,0,0.1);
	padding: 0.5em 2em;
	font-size:1em;
	font-weight: normal;
	line-height: 1;
	text-decoration: none;
	text-align: center;
	cursor: pointer;
	box-sizing: border-box;
	align-items: stretch;
	font-family: sans-serif;
	transition:
		color 0.1s,
		background-color 0.1s,
		transform 0.1s
	;

	&:focus,
	&:hover{
		text-decoration: none;
		font-weight: normal;
		color: #666;
		background-color: #f9f9f9;
		outline: none;
		// transform: scale(1.04, 1.04);
	}
	&:hover{
		background-color: #f0f0f0;
	}
	&:active{
		background-color: #333;
		color: #fff;
		box-shadow: inset 0 3px 15px rgba(0,0,0,0.3);
		transform: translateY(1px);
		// transform: translateY(1px) scale(1.04, 1.04);
	}
}

.px2-btn{

	&.px2-btn--primary{
		border-color: #00a0e6;
		background-color: #f5fbfe; // rgba(0,160,230,0.05);
		color: #00a0e6;
		&:focus,
		&:hover{
			background-color: #d9f1fb; // rgba(0,160,230,0.15);
		}
		&:hover{
			background-color: #ccecfa; // rgba(0,160,230,0.3);
		}
		&:active{
			background-color: #00a0e6;
			color: #fff;
		}
	}
	&.px2-btn--secondary{
		border-color: #999;
		background: #fff;
		color: #999;
		&:focus,
		&:hover{
			background-color: #f9f9f9;
		}
		&:hover{
			background-color: #f0f0f0;
		}
		&:active{
			background-color: #333;
			color: #fff;
		}
	}
	&.px2-btn--danger{
		border-color: #e86a60;
		background-color: #fdf8f8; // rgba(232,106,96,0.05);
		color: #e86a60;
		&:focus,
		&:hover{
			background-color: #fce9e7; // rgba(232,106,96,0.15);
		}
		&:hover{
			background-color: #fae1df; // rgba(232,106,96,0.3);
		}
		&:active{
			background-color: #e86a60;
			color: #fff;
		}
	}
	&.px2-btn--download{
		border-color: #4ad64d;
		background-color: #f7fcf7; // rgba(74,214,77,0.05);
		color: #4ad64d;
		&:focus,
		&:hover{
			background-color: #e4f9e4; // rgba(74,214,77,0.15);
		}
		&:hover{
			background-color: #dbf7db; // rgba(74,214,77,0.3);
		}
		&:active{
			background-color: #4ad64d;
			color: #fff;
		}
	}

	&[disabled]{
		border-color: #ddd;
		background-color: rgba(153,153,153,0.05);
		color: #ddd;
		cursor: default;
		transform: none;
		&:focus,
		&:hover{
			border-color: #ddd;
			background-color: rgba(153,153,153,0.05);
			color: #ddd;
			cursor: default;
		}
	}
}

.px2-horizontal-list{
	padding: 0;
	li{
		display: inline-block;
		list-style-type: none;
		margin: 0.1em 0.3em;
	}
}

.px2-horizontal-list{
	&--left{
		text-align: left;
	}
	&--center{
		text-align: center;
	}
	&--right{
		text-align: right;
	}
}

.px2-table{
	border: none;
	border-collapse: collapse;
	text-align: left;

	th,
	td {
		border: 1px solid #999999;
		background: #ffffff;
		padding: 10px;
	}

	th {
		background: #e7e7e7;
	}

	thead th,
	tfoot th {
		background: #d9d9d9;
		text-align: center;
	}

	thead td,
	tfoot td {
		background: #eeeeee;
	}
}

.px2-modal{
	display: block;
	background: rgba(0,0,0,0.5);
	position: absolute; left: 0; top: 0;
	width: 100%;
	height: 100%;
	z-index: 1000000;

	form{
		display: inline;
	}

	&__dialog{
		position: relative;
		display: flex;
		flex-direction: column;
		top: 0; left: 0;
		background-color: #fff;
		box-sizing: border-box;
		padding: 10px 0;
		margin: 20px auto;
		width: calc(100% - 40px);
		max-height: calc(100% - 40px);
		overflow: auto;
		height: auto;
	}

	&__header{
		border-bottom: 1px solid var(--px2-main-color);
		padding: 0 20px;
		margin-bottom: 0.5em;
	}

	&__body{
		position: relative;
		top: 0; left: 0;
		padding: 0 20px;
		overflow: auto;
	}

	&__title{
		font-weight: bold;
		font-size: large;
	}

	&__footer{
		border-top: 1px solid var(--px2-main-color);
		margin: 10px 0 0 0;
		padding: 10px 20px 0 20px;
		>ul{
			display: flex;
			justify-content: flex-end;
			margin: 0;
			padding: 0;
			>li{
				list-style-type: none;
				margin: 0 0 0 5px;
				padding: 0;
			}
		}
	}
}

@mixin px2-editor-type{
	display: inline-block;
	font-size: 12px;
	line-height: 1;
	padding: 0.4em 1em;
	text-align: center;
	color: #fff;
}
.px2-editor-type{
	@include px2-editor-type;

	&__html, // <- deprecated (古い書き方ですが、後方互換のために残します)
	&--html{
		@include px2-editor-type;
		background-color: #4a8a98;
		&:before{
			content: "HTML";
		}
	}
	&__html-gui, // <- deprecated (古い書き方ですが、後方互換のために残します)
	&--html-gui{
		@include px2-editor-type;
		background-color: #9f5b75;
		&:before{
			content: "GUI";
		}
	}
	&__txt, // <- deprecated (古い書き方ですが、後方互換のために残します)
	&--txt{
		@include px2-editor-type;
		background-color: #999;
		&:before{
			content: "TEXT";
		}
	}
	&__md, // <- deprecated (古い書き方ですが、後方互換のために残します)
	&--md{
		@include px2-editor-type;
		background-color: #ae9b59;
		&:before{
			content: "Markdown";
		}
	}
	&__jade, // <- deprecated (古い書き方ですが、後方互換のために残します)
	&--jade{
		@include px2-editor-type;
		background-color: #999;
		&:before{
			content: "Jade";
		}
	}
	&__not-exists, // <- deprecated (古い書き方ですが、後方互換のために残します)
	&--not-exists{
		@include px2-editor-type;
		background-color: #c3c3c3;
		&:before{
			content: "未作成";
		}
	}
	&__page-not-exists, // <- deprecated (古い書き方ですが、後方互換のために残します)
	&--page-not-exists{
		@include px2-editor-type;
		background-color: #c3c3c3;
		&:before{
			content: "ページ未作成";
		}
	}
	&__alias, // <- deprecated (古い書き方ですが、後方互換のために残します)
	&--alias{
		@include px2-editor-type;
		background-color: #c3c3c3;
		&:before{
			content: "ALIAS";
		}
	}
	&--fullwidth{
		width: auto;
		display: block;
		float:none;
	}
}

@keyframes px2-loading {
	0% {
		transform: scale(0.0);
	}
	100% {
		transform: scale(1.0);
		opacity: 0;
	}
}

.px2-loading{
	clear: both;
	float: none;
	display: flex;
	justify-content: center;
	align-content: center;
	text-align:center;
	padding:80px;

	&::before{
		display: block;
		content: "";

		background-color: var(--px2-main-color);
		width: 15px;
		height: 15px;
		border-radius: 100%;
		margin: 2px;
		animation-fill-mode: both;

		display: inline-block;
		height: 60px;
		width: 60px;
		animation: px2-loading 1s 0s ease-in-out infinite;

	}
}

.px2-document{
	font-size: medium;
	line-height: 1.5;

	// paragraph
	p{
		margin-top: 1em;
		margin-bottom: 1em;
	}

	// headers
	h1{
		font-weight:bold;
		margin-top: 2em;
		margin-bottom: 0.5em;
	}
	h2, h3, h4, h5, h6{
		font-weight:bold;
		margin-top: 1.5em;
		margin-bottom: 0.5em;
	}
	h1{font-size: 240%;}
	h2{font-size: 180%;}
	h3{font-size: 160%;}
	h4{font-size: 130%;}
	h5{font-size: 120%;}
	h6{font-size: 110%;}

	// lists
	ul, ol {
		display: block;
		margin-top: 1em;
		margin-bottom: 1em;
		padding-left: 40px;
		ul, ol {
			margin-top: 0;
			margin-bottom: 0;
		}
	}
}

.px2-header{
	height: 30px;

	&__inner{
		display: flex;
		justify-content: space-between;
		background-color: #fff;
		padding: 0; margin:0;
		position: fixed;
		top: 0; left: 0;
		width: 100%;
		min-height: 10px;
		z-index: 100;
		border-bottom: 3px solid var(--px2-main-color);
		box-shadow: 0 5px 13px rgba(0, 0, 0, 0.1);
		color: #666;
		line-height: 1;
		box-sizing: border-box;
		border-bottom-color: var(--px2-main-color);
		color: var(--px2-main-color);
	}

	a{
		color: #333;
		text-decoration: underline;
		&:hover{
			color: #000;
			text-decoration: none;
		}
	}

	// LOGO
	&__px2logo{
		transition: min-width 0.3s ease-out 0s , width 0.3s ease-out 0s , height 0.3s ease-out 0s;
		position: relative;
		left: 0px; top: 0px;
		padding: 6px;
		margin-left: 6px;
		width: 45px; height: auto;
		box-sizing: border-box;
		a{
			display: inline-block;
			position: absolute;
			padding: 6px;
			background-color: #fff;
			left: 0; top: 0;
			transition: min-width 0.3s ease-out 0s , width 0.3s ease-out 0s , height 0.3s ease-out 0s;
			width: 45px; height: 45px;
			box-sizing: border-box;

			img, svg{
				width: 100%; height: 100%;
			}
		}
	} // px2logo

	&__block{
		display: flex;
		flex-wrap: wrap;
		flex-grow: 100;
		justify-content: space-between;
	}

	&__id{
		width: auto;
		margin: 0;
		padding: 10px 0 0 0;
		font-size: 13px;
		transition: opacity 0.5s ease 0;
		*{
			color:var(--px2-main-color);
			margin:0 0.5em;
			display: inline-block;
			text-shadow: none;
			font-weight: normal;
		}
		a{
			color:var(--px2-main-color);
			text-decoration: none;
			&:hover{
				color: #000;
				text-decoration: none;
			}
		}
	} // id

	// グローバルメニュー
	&__global-menu{
		padding: 0;
		margin: 0;
		margin-right: 35px;
		margin-bottom: -3px;
		line-height: 1;

		ul{
			display:flex;
			flex-wrap: wrap;
			justify-content: flex-end;
			margin:0; padding:0;

			li{
				margin: 0 1px 0 1px;
				padding: 0;
				list-style-type: none;
				font-size: 13px;
				position: relative;

				a,span{
					display: block;
					margin:0;
					padding:12px 2em 5px;
					color: #666;
					border: none;
					font-weight: normal;
					text-decoration: none;
					border-bottom: 3px solid transparent;

					&:hover{
						color:var(--px2-main-color);
					}
					&.current{
						font-weight: bold;
						border-bottom-color: rgba(255, 255, 255, 0.85);
						color:var(--px2-main-color);
						padding-left: 1em;
						&:before{
							content:' ';
							width:0;
							height:0;
							display: inline-block;
							margin-right: 0.5em;
							border-width:0.3em 0 0.3em 0.5em;
							border-style:solid;
							border-color:var(--px2-main-color);
							border-top-color:transparent;
							border-bottom-color:transparent;
						}
					}
				}
				span{
					padding-left: 0;
					padding-right: 0;
				}
			}
			ul{
				display: none;
				position: absolute;
				right: 0; top: 100%;
			}

			li.px2-header__global-menu-group{
				>a::after{
					content:' ';
					width:0;
					height:0;
					display: inline-block;
					margin-left: 0.3em;
					border-width:0.3em 0 0.3em 0.5em;
					border-style:solid;
					border-color:#666;
					border-top-color:transparent;
					border-bottom-color:transparent;
					transition: transform 0.2s ease-in;
					transform: rotate(90deg);
				}
				ul{
					padding: 0;
					background-color: #999;

					li{
						list-style-type: none;
						a{
							font-weight: normal;
							padding: 0.8em 2em;
							color: #eee;
							background-color: #333;
							text-decoration: none;
							&:hover{
								background-color: #666;
								color: #fff;
							}
							&.current{
								font-weight: bold;
								padding: 0.8em 2em;
								background-color:var(--px2-main-color);
								color: #fff;
								border: none;
								&:before{
									content:none;
								}
							}
						}
					}
				}
			}
			li.px2-header__global-menu-group-opened {
				z-index: 10000;
			}
			li.px2-header__global-menu-group-opened > a::after{
				transform: rotate(90deg);
			}
		}
	} // global-menu

	// ハンバーガーメニュー
	&__shoulder-menu{
		position: fixed; top: 0; right: 0;
		padding: 0; margin: 0;
		overflow: hidden;
		>button{
			background: #f9f9f9;
			border: none;
			border-radius: 0;
			border-left: 1px solid var(--px2-main-color);
			position: absolute; top:0; right:0;
			height: 30px;
			width: 30px;
			box-sizing: border-box;
			cursor: pointer;
		}
		>ul{
			position: absolute;
			top: 30px; right: 0;
			display: none;
			margin: 0; padding: 0;
			list-style-type: none;
			background-color: #333;
			border: 1px solid #aaa;
			overflow: auto;
			width:280px;
			max-width:100%;
			opacity: 0.95;

			li{
				margin: 0 0 1px 0;
				border-bottom: 1px solid #555;
				font-size: 13px;
				&:last-child{
					border: none;
				}
				a{
					display:block;
					font-weight: normal;
					padding: 0.8em 2em;
					color: #eee;
					background-color: #333;
					text-decoration: none;
					&:hover{
						background-color: #444;
						color: #fff;
					}
					&.current{
						font-weight: bold;
						background-color:var(--px2-main-color);
						color: #fff;
					}
				}
			}
			li.px2-header__shoulder-menu-group{
				>a::after{
					content:' ';
					width:0;
					height:0;
					display: inline-block;
					margin-left: 1em;
					border-width:0.3em 0 0.3em 0.5em;
					border-style:solid;
					border-color:#eee;
					border-top-color:transparent;
					border-bottom-color:transparent;
					transition: transform 0.2s ease-in;
				}
				ul{
					padding: 0 0 0 15px;
					display:none;
					li{
						list-style-type: none;
						a{
							background-color: #555;
							color: #ddd;
							&:hover{
								background-color: #666;
								color: #fff;
							}
							&.current{
								font-weight: bold;
								background-color:var(--px2-main-color);
								color: #fff;
							}
						}
					}
				}
			}
			li.px2-header__shoulder-menu-group-opened > a::after{
				transform: rotate(90deg);
			}

		}
	} // shoulder-menu

	// Humburger Menu Icon
	&__hamburger,
	&__hamburger:before,
	&__hamburger:after {
		display: block;
		position: absolute;
		height: 2px;
		width: auto;
		border-radius: 1px;
		background-color: #555;
		// background-color: var(--px2-main-color);
		content: '';
	}
	&__hamburger:before {
		bottom: -8px;
	}
	&__hamburger:after {
		bottom: 8px;
	}

}

