sections.css:
.px2-link.px2-link--burette{
	&:before{
		content:' ';
		width:0;
		height:0;
		display: inline-block;
		margin-right: 0.2em;
		border-width:0.35em 0 0.35em 0.6em;
		border-style:solid;
		border-color:#00a0e6;
		border-top-color:transparent;
		border-bottom-color:transparent;
	}
}

.px2-btn{
    display: inline-block;
    border-radius: 1px;
    background-color: #fff;
    color: #666;
    border: 1px solid #666;
    box-shadow: 0 2px 0px rgba(0,0,0,0.1);
    // text-shadow: 0px 0px 3px rgba(0,0,0,0.1);
    padding: 0.5em 2em;
    font-size:1em;
    font-weight: normal;
    line-height: 1;
    text-decoration: none;
    text-align: center;
    cursor: pointer;
    box-sizing: border-box;
    align-items: stretch;
    font-family: sans-serif;

    &:focus,
    &:hover{
        text-decoration: none;
        font-weight: normal;
        color: #666;
        background-color: #f9f9f9;
        outline: none;
    }
    &:hover{
        background-color: #f0f0f0;
    }
    &:active{
        background-color: #333;
        color: #fff;
        box-shadow: inset 0 3px 15px rgba(0,0,0,0.3);
    }

    &.px2-btn--primary{
        border-color: #00a0e6;
        background-color: #f5fbfe; // rgba(0,160,230,0.05);
        color: #00a0e6;
        &:focus,
        &:hover{
            background-color: #d9f1fb; // rgba(0,160,230,0.15);
        }
        &:hover{
            background-color: #ccecfa; // rgba(0,160,230,0.3);
        }
        &:active{
            background-color: #00a0e6;
            color: #fff;
        }
    }
    &.px2-btn--secondary{
        border-color: #999;
        background: #fff;
        color: #999;
        &:focus,
        &:hover{
            background-color: #f9f9f9;
        }
        &:hover{
            background-color: #f0f0f0;
        }
        &:active{
            background-color: #333;
            color: #fff;
        }
    }
    &.px2-btn--danger{
        border-color: #e86a60;
        background-color: #fdf8f8; // rgba(232,106,96,0.05);
        color: #e86a60;
        &:focus,
        &:hover{
            background-color: #fce9e7; // rgba(232,106,96,0.15);
        }
        &:hover{
            background-color: #fae1df; // rgba(232,106,96,0.3);
        }
        &:active{
            background-color: #e86a60;
            color: #fff;
        }
    }
    &.px2-btn--download{
        border-color: #4ad64d;
        background-color: #f7fcf7; // rgba(74,214,77,0.05);
        color: #4ad64d;
        &:focus,
        &:hover{
            background-color: #e4f9e4; // rgba(74,214,77,0.15);
        }
        &:hover{
            background-color: #dbf7db; // rgba(74,214,77,0.3);
        }
        &:active{
            background-color: #4ad64d;
            color: #fff;
        }
    }

    &--sm{
        font-size:0.8em;
    }
    &--lg{
        font-size:1.2em;
    }
    &--block{
        display: block;
        width: 100%;
    }


    &[disabled]{
        border-color: #ddd;
        background-color: rgba(153,153,153,0.05);
        color: #ddd;
        cursor: default;
        &:focus,
        &:hover{
            border-color: #ddd;
            background-color: rgba(153,153,153,0.05);
            color: #ddd;
            cursor: default;
        }
    }
}

.px2-table{
	border: none;
	border-collapse: collapse;
	text-align: left;

	th,
	td {
		border: 1px solid #999999;
		background: #ffffff;
		padding: 10px;
	}

	th {
		background: #e7e7e7;
	}

	thead th,
	tfoot th {
		background: #d9d9d9;
		text-align: center;
	}

	thead td,
	tfoot td {
		background: #eeeeee;
	}
}

.px2-modal{
    display: block;
    background: rgba(0,0,0,0.5);
    position: absolute; left: 0; top: 0;
    width: 100%;
    height: 100%;
    z-index: 10000;

    &__dialog{
        position: relative;
        display: flex;
        flex-direction: column;
        top: 0; left: 0;
        background-color: #fff;
        box-sizing: border-box;
        padding: 10px 0;
        margin: 20px auto;
        width: calc(100% - 40px);
        max-height: calc(100% - 40px);
        overflow: auto;
        height: auto;
    }

    &__header{
        border-bottom: 1px solid #00a0e6;
        padding: 0 20px;
    }

    &__body{
        position: relative;
        top: 0; left: 0;
        padding: 0 20px;
        overflow: auto;
    }

    &__title{
        font-weight: bold;
        font-size: large;
    }

    &__footer{
        border-top: 1px solid #00a0e6;
        margin: 10px 0 0 0;
        padding: 10px 20px 0 20px;
        >ul{
            display: flex;
            justify-content: flex-end;
            margin: 0;
            padding: 0;
            >li{
                list-style-type: none;
                margin: 0 0 0 5px;
                padding: 0;
            }
        }
    }
}

@mixin px2-editor-type{
	display: inline-block;
	font-size: 12px;
	line-height: 1;
	padding: 0.4em 1em;
	text-align: center;
	color: #fff;
}
.px2-editor-type__html{
	@include px2-editor-type;
	background-color: #4a8a98;
	&:before{
		content: "HTML";
	}
}
.px2-editor-type__html-gui{
	@include px2-editor-type;
	background-color: #9f5b75;
	&:before{
		content: "GUI";
	}
}
.px2-editor-type__txt{
	@include px2-editor-type;
	background-color: #999;
	&:before{
		content: "TEXT";
	}
}
.px2-editor-type__md{
	@include px2-editor-type;
	background-color: #ae9b59;
	&:before{
		content: "Markdown";
	}
}
.px2-editor-type__jade{
	@include px2-editor-type;
	background-color: #999;
	&:before{
		content: "Jade";
	}
}
.px2-editor-type__not-exists{
	@include px2-editor-type;
	background-color: #c3c3c3;
	&:before{
		content: "未作成";
	}
}
.px2-editor-type__page-not-exists{
	@include px2-editor-type;
	background-color: #c3c3c3;
	&:before{
		content: "ページ未作成";
	}
}
.px2-editor-type--fullwidth{
	width: auto;
	display: block;
	float:none;
}

.px2-loading{
	clear: both;
	float: none;
	display: block;
	text-align:center;
	padding:80px;
	&:before{
		display: block;
		text-align: center;
		content: ' ';
		background-color: #000;
		width: 150px; height: 1px;
		margin: 0 auto;

		animation: broccoli--progress 1.4s infinite normal ease;
		@keyframes broccoli--progress {
			0% { transform: rotate(0deg) scale(0,0); }
			30% { transform: rotate(270deg) scale(1,1); }
			100% { transform: rotate(720deg) scale(0,0); }
		}
	}
}

.px2-document{
	font-size: medium;
	line-height: 1.5;

	// paragraph
	p{
		margin-top: 1em;
		margin-bottom: 1em;
	}

	// headers
	h1{
		font-weight:bold;
		margin-top: 2em;
		margin-bottom: 0.5em;
	}
	h2, h3, h4, h5, h6{
		font-weight:bold;
		margin-top: 1.5em;
		margin-bottom: 0.5em;
	}
	h1{font-size: 240%;}
	h2{font-size: 180%;}
	h3{font-size: 160%;}
	h4{font-size: 130%;}
	h5{font-size: 120%;}
	h6{font-size: 110%;}

	// lists
	ul, ol {
		display: block;
		margin-top: 1em;
		margin-bottom: 1em;
		padding-left: 40px;
		ul, ol {
			margin-top: 0;
			margin-bottom: 0;
		}
	}
}

