sections.css:
.px2-btn{
    display: inline-block;
    border-radius: 3px;
    background-color: #393939;
    color: #fff;
    border: 1px solid #393939;
    box-shadow: 0 3px 3px rgba(0,0,0,0.2);
    padding: 0.5em 2em;
    font-size:1em;
    line-height: 1;
    text-decoration: none;
    text-align: center;
    cursor: pointer;
    box-sizing: border-box;
    align-items: stretch;
    font-family: sans-serif;

    &:focus,
    &:hover{
        background-color: #222;
        color: #eee;
        outline: none;
    }
    &:focus{
        box-shadow: inset 0 3px 5px rgba(0,0,0,.125);
    }

    &.px2-btn--primary{
        background: linear-gradient(#30afce, #2699c0);
        border: 1px solid #30afce;
        &:focus,
        &:hover{
            background: linear-gradient(#219ebc, #1985aa);
        }
    }
    &.px2-btn--secondary{
        background: linear-gradient(#fff, #ececec);
        border: 1px solid #a8a8a8;
        color: #4ca8d6;
        &:focus,
        &:hover{
            background: linear-gradient(#f8f8f8, #e0e0e0);
            color: #3394c4;
        }
    }
    &.px2-btn--danger{
        background: linear-gradient(#e86a60, #da5a50);
        border: 1px solid #e86a60;
        &:focus,
        &:hover{
            background: linear-gradient(#d34f45, #c74339);
        }
    }
    &.px2-btn--download{
        background: linear-gradient(#4ad64d, #3dc941);
        border: 1px solid #4ad64d;
        &:focus,
        &:hover{
            background: linear-gradient(#33be36, #29b12c);
        }
    }

    &--sm{
        font-size:0.8em;
    }
    &--lg{
        font-size:1.2em;
    }
    &--block{
        display: block;
        width: 100%;
    }
}

@mixin px2-editor-type{
    display: inline-block;
    font-size: 12px;
    line-height: 1;
    padding: 0.4em 1em;
    text-align: center;
    color: #fff;
}
.px2-editor-type__html{
    @include px2-editor-type;
    background-color: #4a8a98;
    &:before{
        content: "HTML";
    }
}
.px2-editor-type__html-gui{
    @include px2-editor-type;
    background-color: #9f5b75;
    &:before{
        content: "GUI";
    }
}
.px2-editor-type__txt{
    @include px2-editor-type;
    background-color: #999;
    &:before{
        content: "TEXT";
    }
}
.px2-editor-type__md{
    @include px2-editor-type;
    background-color: #ae9b59;
    &:before{
        content: "Markdown";
    }
}
.px2-editor-type__jade{
    @include px2-editor-type;
    background-color: #999;
    &:before{
        content: "Jade";
    }
}
.px2-editor-type__not-exists{
    @include px2-editor-type;
    background-color: #c3c3c3;
    &:before{
        content: "未作成";
    }
}
.px2-editor-type__page-not-exists{
    @include px2-editor-type;
    background-color: #c3c3c3;
    &:before{
        content: "ページ未作成";
    }
}
.px2-editor-type--fullwidth{
    width: auto;
    display: block;
    float:none;
}

@keyframes px2-loading {
	0% { transform: rotate(360deg); }
	100% { transform: rotate(0deg); }
}
.px2-loading{
	clear: both;
	float: none;
	display: block;
	text-align:center;
	padding:3em;
}
.px2-loading:before{
	display: block;
	text-align: center;
	content: url('images/loading.svg');
	width: 50px; height: 50px;
	margin: 0 auto;
	-webkit-animation: px2-loading 1s infinite normal linear;
}

