sections.css:
.px2-a,
.px2-link.px2-link--burette, // スペル誤り
.px2-link.px2-link--bullet{ // 2.0.11 にて修正
	&:before{
		content:' ';
		width:0;
		height:0;
		display: inline-block;
		margin-right: 0.2em;
		border-width:0.35em 0 0.35em 0.6em;
		border-style:solid;
		border-color:var(--px2-main-color);
		border-top-color:transparent;
		border-bottom-color:transparent;
	}
}

.px2-btn{
	display: inline-block;
	border-radius: 3px;
	background-color: $background-color;
	color: $text-color;
	border: 1px solid $text-color;
	box-shadow: 0 2px 0px rgba(0,0,0,0.1);
	// text-shadow: 0px 0px 3px rgba(0,0,0,0.1);
	padding: 0.5em 2em;
	font-size:1em;
	font-weight: normal;
	font-family: $px2-font-family;
	line-height: 1;
	text-decoration: none;
	text-align: center;
	cursor: pointer;
	box-sizing: border-box;
	align-items: stretch;
	transition:
		color 0.1s,
		background-color 0.1s,
		transform 0.1s
	;

	&:focus,
	&:hover{
		text-decoration: none;
		font-weight: normal;
		color: #666;
		background-color: #f3f3f3;
		// transform: scale(1.04, 1.04);
	}
	&:focus{
		box-shadow: 0 0 0 .2rem rgba(0,123,255,.25);
	}
	&:hover{
		background-color: #f0f0f0;
	}
	&:active{
		background-color: $text-color;
		color: $background-color;
		box-shadow: inset 0 3px 15px rgba(0,0,0,0.3);
		transform: translateY(1px);
		// transform: translateY(1px) scale(1.04, 1.04);
	}
}

.px2-btn{

	&.px2-btn--primary{
		border-color: #00a0e6;
		background-color: #f5fbfe; // rgba(0,160,230,0.05);
		color: #00a0e6;
		&:focus,
		&:hover{
			background-color: #d9f1fb; // rgba(0,160,230,0.15);
		}
		&:hover{
			background-color: #ccecfa; // rgba(0,160,230,0.3);
		}
		&:active{
			background-color: #00a0e6;
			color: #fff;
		}
	}
	&.px2-btn--secondary{
		border-color: $text-color;
		background: $background-color;
		color: $text-color;
		&:focus,
		&:hover{
			background-color: #f3f3f3;
		}
		&:hover{
			background-color: #f0f0f0;
		}
		&:active{
			background-color: $text-color;
			color: $background-color;
		}
	}
	&.px2-btn--danger{
		border-color: #e86a60;
		background-color: #fdf8f8; // rgba(232,106,96,0.05);
		color: #e86a60;
		&:focus,
		&:hover{
			background-color: #fce9e7; // rgba(232,106,96,0.15);
		}
		&:hover{
			background-color: #fae1df; // rgba(232,106,96,0.3);
		}
		&:active{
			background-color: #e86a60;
			color: #fff;
		}
	}
	&.px2-btn--download{
		border-color: #4ad64d;
		background-color: #f7fcf7; // rgba(74,214,77,0.05);
		color: #4ad64d;
		&:focus,
		&:hover{
			background-color: #e4f9e4; // rgba(74,214,77,0.15);
		}
		&:hover{
			background-color: #dbf7db; // rgba(74,214,77,0.3);
		}
		&:active{
			background-color: #4ad64d;
			color: #fff;
		}
	}

	&[disabled]{
		border-color: #ddd;
		background-color: rgba(153,153,153,0.05);
		color: #ddd;
		cursor: default;
		transform: none;
		&:focus,
		&:hover,
		&:active{
			border-color: #ddd;
			background-color: rgba(153,153,153,0.05);
			color: #ddd;
			cursor: default;
			box-shadow: 0 2px 0px rgba(0,0,0,0.1);
			transform: none;
		}
	}
}

.px2-btn{
	&--toggle-on{
		box-shadow: inset 0 3px 15px rgba(0,0,0,0.15);
		transform: translateY(1px);
		// transform: translateY(1px) scale(1.04, 1.04);

		&[disabled]{
			box-shadow: inset 0 3px 15px rgba(0,0,0,0.15);
			transform: translateY(1px);
			&:focus,
			&:hover,
			&:active{
				box-shadow: inset 0 3px 15px rgba(0,0,0,0.15);
				transform: translateY(1px);
			}
		}
	}
}

.px2-btn{

	&--sm{
		font-size:0.8em;
	}
	&--lg{
		font-size:1.2em;
	}
	&--block{
		display: block;
		width: 100%;
	}

}

.px2-list{
	padding: 0 0 0 40px;
	li{
		list-style-type: disc;
	}
}

.px2-horizontal-list,
.px2-horizontal-list>ul{
	display: flex;
	flex-wrap: wrap;
	margin-top: 1em;
	margin-bottom: 1em;
	padding-left: 0;

	>ul>li ,
	>li {
		list-style-type: none;
		padding-left: 0;
		padding-right: 1em;
		>p{
			margin: 0;
		}
	}
}

.px2-horizontal-list{
	&--left{
		justify-content: flex-start;
	}
	&--center{
		justify-content: center;
	}
	&--right{
		justify-content: flex-end;
	}
}

.px2-vertical-list,
.px2-vertical-list>ul{
	display: flex;
	flex-wrap: wrap;
	flex-direction: column;
	margin-top: 0.2em;
	margin-bottom: 0.2em;
	padding-left: 0;

	>ul>li ,
	>li {
		list-style-type: none;
		padding-left: 0;
		>p{
			margin: 0;
		}
	}
}

.px2-ordered-list{
	padding: 0 0 0 40px;
	li{
		list-style-type: decimal;
	}
}

.px2-note-list,
.px2-note-list>ul{
	margin-top: 1em;
	margin-bottom: 1em;
	padding-left: 1.5em;

	>ul>li,
	>li {
		list-style-type: none;
		color: #999;
		font-size: 86%;
		position: relative;
		&::before{
			content: "※";
			position: absolute;
			left: -1.5em;
		}
	}
}

.px2-table{
	border: none;
	border-collapse: collapse;
	text-align: left;

	th,
	td {
		border: 1px solid #999999;
		background: $background-color;
		padding: 10px;
	}

	th {
		background: #e7e7e7;
	}

	thead th,
	tfoot th {
		background: #d9d9d9;
		text-align: center;
	}

	thead td,
	tfoot td {
		background: #eeeeee;
	}
}

.px2-notice {
	margin-top: 1.5em;
	margin-bottom: 1.5em;
	padding: 0.75em 1.25em;
	border-radius: 0.25em;

	color: #004085;
	background-color: #cce5ff;
	border: 1px solid #b8daff;

	*{
		margin-top: 0;
		margin-bottom: 0;
	}
}
.px2-notice.px2-notice--success{
	color: #155724;
	background-color: #d4edda;
	border-color: #c3e6cb;
}
.px2-notice.px2-notice--warning{
	color: #856404;
	background-color: #fff3cd;
	border-color: #ffeeba;
}
.px2-notice.px2-notice--danger{
	color: #721c24;
	background-color: #f8d7da;
	border-color: #f5c6cb;
}
.px2-notice.px2-notice--info{
	color: #0c5460;
	background-color: #d1ecf1;
	border-color: #bee5eb;
}

input[type=text].px2-input,
input[type=password].px2-input,
input[type=number].px2-input,
input[type=search].px2-input,
input[type=tel].px2-input,
input[type=url].px2-input,
input[type=email].px2-input,
input[type=datetime].px2-input,
input[type=date].px2-input,
input[type=month].px2-input,
input[type=week].px2-input,
input[type=time].px2-input,
input[type=color].px2-input,
input[type=datetime-local].px2-input,
textarea.px2-input,
select.px2-input{
    display: inline-block;
    box-sizing: border-box;
    width: auto;
    min-width: 50px;
    max-width: 100%;
    padding: .375rem .75rem;
    font-size: 1em;
    font-weight: normal;
    line-height: 1.5;
    color: $text-color;
    background-color: $background-color;
    background-clip: padding-box;
    border: 1px solid #ced4da;
    border-radius: .25rem;
    transition: border-color .15s ease-in-out,box-shadow .15s ease-in-out;
    &:focus{
        color: #495057;
        background-color: #fff;
        border-color: #80bdff;
        outline: 0;
        box-shadow: 0 0 0 .2rem rgba(0,123,255,.25);
    }
    &[readonly]{
        background-color: #eee;
    }
    &[disabled]{
        color: #8e959c;
        background-color: #eee;
    }
    &--block{
        display: block;
        width: 100%;
    }
}

.px2-input-group{
    display: inline-flex;
    box-sizing: border-box;
    border-radius: 0px;
    max-width: 100%;

    &__text {
        display: inline-block;
        box-sizing: border-box;
        padding: .375rem .75rem;
        font-size: 1rem;
        font-weight: normal;
        line-height: 1.5;
        color: $text-color;
        background-color: $background-color;
        border: 1px solid #ced4da;
    }

    >*,
    >input.px2-input,
    >select.px2-input,
    >textarea.px2-input,
    >.px2-btn{
        border-right-width: 0px;
        flex-grow: 1;
    }

    >:first-child,
    >input:first-child.px2-input,
    >select:first-child.px2-input,
    >textarea:first-child.px2-input,
    >:first-child.px2-btn{
        border-radius: .25rem 0 0 .25rem / .25rem 0 0 .25rem;
    }
    >:last-child,
    >input:last-child.px2-input,
    >select:last-child.px2-input,
    >textarea:last-child.px2-input,
    >:last-child.px2-btn{
        border-right-width: 1px;
        border-radius: 0 .25rem .25rem 0 / 0 .25rem .25rem 0;
    }

    &--fluid {
        display: flex;
        width: 100%;
    }

}

.px2-modal{
	background: rgba(0,0,0,0.5);
	position: absolute; left: 0; top: 0;
	width: 100%;
	height: 100%;
	z-index: 1000000;
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;
	font-family: $px2-font-family;

	> form{
		// display: inline;
		margin: 0;
		padding: 0;
		width: 100%;
		height: 100%;
		display: flex;
		flex-direction: column;
		justify-content: center;
		align-items: center;
	}

	&__close {
		position: absolute;
		right: -0.5em;
		top: -0.5em;
		width: 1.4em; height: 1.4em;
		button {
			position: absolute;
			left: 0;
			right: 0;
			display: block;
			width: 100%; height: 100%;
			padding: 0;
			margin: 0;
			border-radius: 50%;
			border: 1px solid #999;
			background-color: #eee;
			color: #999;
			cursor: pointer;
			&::before {
				content: "×";
			}
		}
	}

	&__dialog{
		position: relative;
		top: 0; left: 0;
		display: flex;
		flex-direction: column;
		flex-wrap: nowrap;
		color: $text-color;
		background-color: $background-color;
		box-sizing: border-box;
		padding: 10px 0;
		margin: 20px auto;
		width: calc(100% - 40px);
		max-width: 520px;
		height: auto;
		max-height: calc(100% - 40px);
		overflow: visible;
		font-size: medium;
		h1{ font-size: 124%; margin: 0; font-weight: bold; }
		h2{ font-size: 124%; margin: 2em 0 0.5em 0; font-weight: bold; }
		h3{ font-size: 112%; margin: 1em 0 0.5em 0; font-weight: bold; }
		h4{ font-size: 104%; margin: 1em 0 0.5em 0; font-weight: bold; }
		h5{ font-size: 100%; margin: 1em 0 0.5em 0; font-weight: bold; }
		h6{ font-size: 100%; margin: 1em 0 0.5em 0; font-weight: bold; }

		h2{
			border-bottom: 1px solid var(--px2-main-color);
		}
	}

	&__header{
		border-bottom: 1px solid var(--px2-main-color);
		padding: 0 20px;
		margin-bottom: 0.5em;
	}

	&__body{
		position: relative;
		top: 0; left: 0;
		padding: 0 20px;
		overflow: auto;
		flex-grow: 1;
	}

	&__title{
		font-weight: bold;
	}

	&__footer{
		border-top: 1px solid var(--px2-main-color);
		margin: 10px 0 0 0;
		padding: 10px 20px 0 20px;
		display: flex;
		flex-direction: row-reverse;
		justify-content: space-between;

		&-primary>ul,
		&-secondary>ul,
		>ul{
			display: flex;
			justify-content: flex-end;
			margin: 0;
			padding: 0;
			>li{
				list-style-type: none;
				margin: 0 0 0 5px;
				padding: 0;
			}
		}
	}
}

.px2-header{
	height: 45px;
	font-family: $px2-font-family;

	&__inner{
		display: flex;
		justify-content: space-between;
		background-color: #fff;
		padding: 0; margin:0;
		position: fixed;
		top: 0; left: 0;
		width: 100%;
		min-height: 10px;
		z-index: 100;
		border-bottom: 3px solid var(--px2-main-color);
		box-shadow: 0 5px 13px rgba(0, 0, 0, 0.1);
		color: #666;
		line-height: 1;
		box-sizing: border-box;
		border-bottom-color: var(--px2-main-color);
		color: var(--px2-main-color);
	}

	a{
		color: #333;
		text-decoration: underline;
		&:hover{
			color: #000;
			text-decoration: none;
		}
	}

	// LOGO
	&__px2logo{
		transition: min-width 0.3s ease-out 0s , width 0.3s ease-out 0s , height 0.3s ease-out 0s;
		position: relative;
		left: 0px; top: 0px;
		padding: 6px;
		margin-left: 6px;
		width: 45px; height: auto;
		box-sizing: border-box;
		a{
			display: inline-block;
			position: absolute;
			padding: 6px;
			background-color: #fff;
			left: 0; top: 0;
			transition: min-width 0.3s ease-out 0s , width 0.3s ease-out 0s , height 0.3s ease-out 0s;
			width: 45px; height: 45px;
			box-sizing: border-box;

			img, svg{
				width: 100%; height: 100%;
			}
		}
	} // px2logo

	&__block{
		display: flex;
		flex-wrap: wrap;
		flex-grow: 100;
		justify-content: space-between;
	}

	&__id{
		width: auto;
		margin: 0;
		padding: 10px 0 0 0;
		font-size: 13px;
		transition: opacity 0.5s ease 0;
		*{
			color:var(--px2-main-color);
			margin:0 0.5em;
			display: inline-block;
			text-shadow: none;
			font-weight: normal;
		}
		a{
			color:var(--px2-main-color);
			text-decoration: none;
			&:hover{
				color: #000;
				text-decoration: none;
			}
		}
	} // id

	// グローバルメニュー
	&__global-menu{
		padding: 0;
		margin: 0;
		margin-right: 35px;
		margin-bottom: -3px;
		line-height: 1;
		min-height: calc(12px + 5px + 1em);

		ul{
			display:flex;
			flex-wrap: wrap;
			justify-content: flex-end;
			margin:0; padding:0;

			li{
				margin: 0 1px 0 1px;
				padding: 0;
				list-style-type: none;
				font-size: 13px;
				position: relative;

				a,span{
					display: block;
					margin:0;
					padding:12px 2em 5px;
					color: #666;
					border: none;
					font-weight: normal;
					text-decoration: none;
					border-bottom: 3px solid transparent;

					&:hover{
						color:var(--px2-main-color);
					}
					&.current{
						font-weight: bold;
						border-bottom-color: rgba(255, 255, 255, 0.85);
						color:var(--px2-main-color);
						padding-left: 1em;
						&:before{
							content:' ';
							width:0;
							height:0;
							display: inline-block;
							margin-right: 0.5em;
							border-width:0.3em 0 0.3em 0.5em;
							border-style:solid;
							border-color:var(--px2-main-color);
							border-top-color:transparent;
							border-bottom-color:transparent;
						}
					}
				}
				span{
					padding-left: 0;
					padding-right: 0;
				}
			}
			ul{
				display: none;
				position: absolute;
				right: 0; top: 100%;
			}

			li.px2-header__global-menu-group{
				>a::after{
					content:' ';
					width:0;
					height:0;
					display: inline-block;
					margin-left: 0.3em;
					border-width:0.3em 0 0.3em 0.5em;
					border-style:solid;
					border-color:#666;
					border-top-color:transparent;
					border-bottom-color:transparent;
					transition: transform 0.2s ease-in;
					transform: rotate(90deg);
				}
				ul{
					padding: 0;
					background-color: #999;

					li{
						list-style-type: none;
						a{
							font-weight: normal;
							padding: 0.8em 2em;
							color: #eee;
							background-color: #333;
							text-decoration: none;
							&:hover{
								background-color: #666;
								color: #fff;
							}
							&.current{
								font-weight: bold;
								padding: 0.8em 2em;
								background-color:var(--px2-main-color);
								color: #fff;
								border: none;
								&:before{
									content:none;
								}
							}
						}
					}
				}
			}
			li.px2-header__global-menu-group-opened {
				z-index: 10000;
			}
			li.px2-header__global-menu-group-opened > a::after{
				transform: rotate(90deg);
			}
		}
	} // global-menu

	// ハンバーガーメニュー
	&__shoulder-menu{
		position: fixed; top: 0; right: 0;
		padding: 0; margin: 0;
		overflow: hidden;
		>button{
			background: #f9f9f9;
			border: none;
			border-radius: 0;
			border-left: 1px solid var(--px2-main-color);
			position: absolute; top:0; right:0;
			height: 30px;
			width: 30px;
			box-sizing: border-box;
			padding: 0.5em;
			cursor: pointer;
		}
		>ul{
			position: absolute;
			top: 30px; right: 0;
			display: none;
			margin: 0;
			padding: 0;
			list-style-type: none;
			background-color: #333;
			border: 1px solid #aaa;
			overflow: auto;
			width:280px;
			max-width:100%;
			opacity: 0.95;

			li{
				margin: 0 0 1px 0;
				border-bottom: 1px solid #555;
				font-size: 13px;
				&:last-child{
					border: none;
				}
				a{
					display:block;
					font-weight: normal;
					padding: 0.8em 2em;
					color: #eee;
					background-color: #333;
					text-decoration: none;
					&:hover{
						background-color: #444;
						color: #fff;
					}
					&.current{
						font-weight: bold;
						background-color:var(--px2-main-color);
						color: #fff;
					}
				}
			}
			li.px2-header__shoulder-menu-group{
				>a::after{
					content:' ';
					width:0;
					height:0;
					display: inline-block;
					margin-left: 1em;
					border-width:0.3em 0 0.3em 0.5em;
					border-style:solid;
					border-color:#eee;
					border-top-color:transparent;
					border-bottom-color:transparent;
					transition: transform 0.2s ease-in;
				}
				ul{
					padding: 0 0 0 15px;
					margin: 0 0 0 0;
					display:none;
					li{
						list-style-type: none;
						a{
							background-color: #555;
							color: #ddd;
							&:hover{
								background-color: #666;
								color: #fff;
							}
							&.current{
								font-weight: bold;
								background-color:var(--px2-main-color);
								color: #fff;
							}
						}
					}
				}
			}
			li.px2-header__shoulder-menu-group-opened > a::after{
				transform: rotate(90deg);
			}
			>li:last-child{
				margin-bottom: 30px;
			}
		}
	} // shoulder-menu

	// ハンバーガーメニューアイコン
	&__hamburger,
	&__hamburger:before,
	&__hamburger:after {
		display: block;
		position: absolute;
		height: 2px;
		width: calc(100% - 1em);
		border-radius: 1px;
		margin: 0 auto;
		// background: #555;
		background: var(--px2-main-color);
		content: '';
	}
	&__hamburger:before {
		bottom: -6px;
		width: 100%;
	}
	&__hamburger:after {
		bottom: 6px;
		width: 100%;
	}

	// Small Screen
	.px2-header__shoulder-global-menu{
		display: none;
	}
	@media all and (max-width: 640px){
		&__global-menu ul{
			display: none;
		}
		.px2-header__shoulder-global-menu{
			display: block;
		}
	}

}

@mixin px2-editor-type{
	display: inline-block;
	font-size: 12px;
	line-height: 1;
	padding: 0.4em 1em;
	text-align: center;
	color: #fff;
}
.px2-editor-type{
	@include px2-editor-type;

	&__html, // <- deprecated (古い書き方ですが、後方互換のために残します)
	&--html{
		@include px2-editor-type;
		background-color: #4a8a98;
		&:before{
			content: "HTML";
		}
	}
	&__html-gui, // <- deprecated (古い書き方ですが、後方互換のために残します)
	&--html-gui{
		@include px2-editor-type;
		background-color: #9f5b75;
		&:before{
			content: "GUI";
		}
	}
	&__txt, // <- deprecated (古い書き方ですが、後方互換のために残します)
	&--txt{
		@include px2-editor-type;
		background-color: #999;
		&:before{
			content: "TEXT";
		}
	}
	&__md, // <- deprecated (古い書き方ですが、後方互換のために残します)
	&--md{
		@include px2-editor-type;
		background-color: #ae9b59;
		&:before{
			content: "Markdown";
		}
	}
	&__jade, // <- deprecated (古い書き方ですが、後方互換のために残します)
	&--jade{
		@include px2-editor-type;
		background-color: #999;
		&:before{
			content: "Jade";
		}
	}
	&__not-exists, // <- deprecated (古い書き方ですが、後方互換のために残します)
	&--not-exists{
		@include px2-editor-type;
		background-color: #c3c3c3;
		&:before{
			content: "未作成";
		}
	}
	&__page-not-exists, // <- deprecated (古い書き方ですが、後方互換のために残します)
	&--page-not-exists{
		@include px2-editor-type;
		background-color: #c3c3c3;
		&:before{
			content: "ページ未作成";
		}
	}
	&__alias, // <- deprecated (古い書き方ですが、後方互換のために残します)
	&--alias{
		@include px2-editor-type;
		background-color: #c3c3c3;
		&:before{
			content: "ALIAS";
		}
	}
	&--fullwidth{
		width: auto;
		display: block;
		float:none;
	}
}

@keyframes px2-loading {
	0% {
		transform: scale(0.0);
	}
	100% {
		transform: scale(1.0);
		opacity: 0;
	}
}

.px2-loading{
	clear: both;
	float: none;
	display: flex;
	justify-content: center;
	align-content: center;
	text-align:center;
	padding:80px;

	&::before{
		display: block;
		content: "";

		background-color: var(--px2-main-color);
		width: 15px;
		height: 15px;
		border-radius: 100%;
		margin: 2px;
		animation-fill-mode: both;

		display: inline-block;
		height: 60px;
		width: 60px;
		animation: px2-loading 1s 0s ease-in-out infinite;

	}
}

