@mixin px2-grid__col(){
    margin: 0 0.5rem 1em 0.5rem;
    box-sizing: border-box;
}
.px2-grid {
    display: flex;
    flex-wrap: wrap;
    margin: 1em -0.5rem 0 -0.5rem;
        // 上下のマージンはその場に表示される文字の大きさが基準になるべきなので、 rem ではなく em を採用する。
    box-sizing: border-box;

    &__col-1of1{
        @include px2-grid__col();
        width: calc( 100% - 1rem );
    }
    &__col-1of2{
        @include px2-grid__col();
        width: calc( 50% - 1rem );
    }
    &__col-1of3{
        @include px2-grid__col();
        width: calc( 33.33% - 1rem );
    }
    &__col-2of3{
        @include px2-grid__col();
        width: calc( 66.66% - 1rem );
    }
    &__col-1of4{
        @include px2-grid__col();
        width: calc( 25% - 1rem );
    }
    &__col-1of5{
        @include px2-grid__col();
        width: calc( 20% - 1rem );
    }
    &__col-2of5{
        @include px2-grid__col();
        width: calc( 40% - 1rem );
    }
    &__col-3of5{
        @include px2-grid__col();
        width: calc( 60% - 1rem );
    }
    &__col-4of5{
        @include px2-grid__col();
        width: calc( 80% - 1rem );
    }
    &__col-1of6{
        @include px2-grid__col();
        width: calc( 16.66% - 1rem );
    }

    @media all and (max-width: 768px) {
        // "sm" on Bootstrap 4
        &__col-1of5,
        &__col-2of5,
        &__col-3of5,
        &__col-4of5,
        &__col-1of6{
            width: calc( 33.33% - 1rem );
        }
    }

    @media all and (max-width: 660px) {
        &__col-1of4,
        &__col-3of4,
        &__col-1of5,
        &__col-2of5,
        &__col-3of5,
        &__col-4of5,
        &__col-1of6{
            width: calc( 50% - 1rem );
        }
    }

    @media all and (max-width: 544px) {
